sys_variable_value.value:
(function execute(inputs, outputs) {

    var startDate = new GlideDateTime(inputs.inp_start_date);
    var endDate = inputs.inp_end_date ? new GlideDateTime(inputs.inp_end_date) : null;
    var isHalfDay = inputs.is_half_day;

    if (isHalfDay && !endDate) {
        
        outputs.requested_days = 0.5;
        return;
    }

    if (!endDate) {
        endDate = new GlideDateTime(startDate); 
    }

    var currentDate = new GlideDateTime(startDate);
    var count = 0;

    while (currentDate <= endDate) {
        var dayOfWeek = currentDate.getDayOfWeekUTC(); 
        if (dayOfWeek != 6 && dayOfWeek != 7) {
            count++;
        }

        currentDate.addDaysUTC(1);
    }

    outputs.requested_days = count;

})(inputs, outputs);

