sys_variable_value.value:
(function execute(inputs, outputs) {

    var user = inputs.inp_employee;
    var leaveType = inputs.inp_leaveType;
    var reqDays = inputs.inp_reqDays;
    var startDate = inputs.inp_startDate;
    var endDate = inputs.inp_endDate;

    var leaveBucket = new GlideRecord('x_1583402_lems_leave_bucket');
    leaveBucket.addQuery('employee', user);
    leaveBucket.addQuery('leave_type', leaveType);
    leaveBucket.query();

    if (leaveBucket.next()) {
        var newBalance = leaveBucket.balance_leave - reqDays;
        var newTaken = Number(leaveBucket.taken_leave) + Number(reqDays);

        leaveBucket.setValue('balance_leave', newBalance);
        leaveBucket.setValue('taken_leave', newTaken);

        leaveBucket.update();
        
    }

})(inputs, outputs);

