sys_variable_value.value:
(function execute(inputs, outputs) {

    var user = inputs.inp_employee;
    var leaveType = inputs.inp_leaveType;
    var reqDays = inputs.inp_reqDays;
    var startDate = inputs.inp_startDate;
    var endDate = inputs.inp_endDate;

{
    var leaveHistory = new GlideRecord('x_1583402_lems_leave_history');
        leaveHistory.initialize();
        leaveHistory.setValue('employee' , user);
        leaveHistory.setValue('leave_type', leaveType);
        leaveHistory.setValue('start_date', startDate);
        leaveHistory.setValue('end_date', endDate);
        leaveHistory.setValue('duration', reqDays);
        leaveHistory.insert();
}

})(inputs, outputs);

